import { useState } from 'react';
import './App.css'
import Botao from './Botao';

const Numeros = Array.from({ length: 25 }, (e, i) => i + 1).sort(() => Math.random() - 0.5).filter((e, i) => i < 15);






function App() {
const [somador,setSomador] = useState(Numeros)

function Somar(){
  const listaNumeros = somador.map(i=>i+1)
  setSomador(somador)
  console.log(listaNumeros)
}

return (
  <div>
    <div>
      {somador}
    </div>
    <Botao onClick={Somar}>Gerar</Botao>  
    </div>
)

}

export default App;
